const App = () => {
  const dispatch = useDispatch();
  const notes = useSelector(state => state);

  const toggleImportance = id => {
    dispatch(toggleImportanceOf(id));
  };

  return (
    <div>
      <NewNote />
      <ul>
        {notes.map(note =>
          <Note note={note} id={note.id} />
        )}
      </ul>
    </div>
  );
}

export default App;
